fn set_hash(self) -> Block {
    let mut hasher = Sha256::new();
    let header: String = String::new()
        + &self.timestamp + &self.data + &self.prev_block_hash;
    hasher.input_str(&header);

    return Block {
        hash: String::from(hasher.result_str().as_str()),
        ..self
    }
}

